mapper.select:
SELECT b.* FROM ( SELECT rownum rn, a.* FROM ( SELECT s.board_no , m.nick , s.dst1 , FIND_CODE_NAME(s.dst2) dst2 , s.title , s.desct , FIND_CODE_NAME(s.day_div) day_div , FIND_CODE_NAME(s.wage_div) wage_div , s.price , s.img , s.reg_date FROM skilled_expert s LEFT JOIN member m ON s.mem_no = m.mem_no WHERE s.div = #{div} AND s.stts = 'ky'
AND dst1 = #{dst1}
AND dst2 = #{dst2}
AND title LIKE '%${title}%'
AND s.mem_no = #{memNo}
ORDER BY s.reg_date DESC ) a
WHERE rownum <= #{pageNum} * 9) b WHERE b.rn > (#{pageNum}-1) * 9

SELECT count(*)
FROM skilled_expert
WHERE div = #{div}

		AND dst1 = #{dst1}


		AND dst2 = #{dst2}


		AND title LIKE '%${title}%'


		AND mem_no = #{memNo}

mapper.update:
UPDATE skilled_expert
SET dst1 = #{dst1}
	, dst2 = #{dst2}
	, img = #{img}
	, title = #{title}
	, desct = #{desct}
	, day_div = #{dayDiv}
	, wage_div = #{wageDiv}
	, price = #{price}
	, cndt = #{cndt}
	, addr_desct = #{addrDesct}
WHERE board_no = #{boardNo}

UPDATE skilled_expert
SET stts = 'kx'
WHERE board_no = #{boardNo}
