mapper.select:
SELECT b.* FROM ( SELECT rownum rn, a.* FROM ( SELECT s.board_no , m.nick , s.dst1 , s.dst2 , s.title , s.desct , s.day_div , s.wage_div , s.price , s.img , s.reg_date FROM skilled_expert s LEFT JOIN member m ON s.mem_no = m.mem_no WHERE s.div = #{div} AND s.stts = 'ky'
AND dst1 = #{dst1}
ANd dst2 = #{dst2}
ORDER BY s.reg_date DESC ) a
WHERE rownum <= #{pageNum} * 9) b WHERE b.rn > (#{pageNum}-1) * 9

SELECT count(*)
FROM skilled_expert
WHERE div = #{div}

mapper.update:
UPDATE skilled_expert
SET dst1 = #{dst1}
	, dst2 = #{dst2}
	, img = #{img}
	, title = #{title}
	, desct = #{desct}
	, day_div = #{dayDiv}
	, wage_div = #{wageDiv}
	, price = #{price}
	, cndt = #{cndt}
	, addr_desct = #{addrDesct}
WHERE board_no = #{boardNo}
