mapper.select:
SELECT b.* FROM ( SELECT rownum rn, a.* FROM ( SELECT b.board_no , m.nick , b.post_ctg , b.dst1 , b.dst2 , b.title , b.img , b.wrt_date , b.hit_count , COUNT(r.mem_no) AS recom_count FROM board b LEFT JOIN member m ON b.mem_no = m.mem_no LEFT JOIN recommendation r ON b.board_no = r.board_no WHERE b.post_ctg = #{postCtg}
AND dst1 = #{dst1}
ANd dst2 = #{dst2}
GROUP BY b.board_no , m.nick , b.post_ctg , b.dst1 , b.dst2 , b.title , b.img , b.wrt_date , b.hit_count ORDER BY b.wrt_date DESC ) a
WHERE rownum <= #{pageNum} * 9) b WHERE b.rn > (#{pageNum}-1) * 9

SELECT count(*)
FROM board
WHERE post_ctg = #{postCtg}

SELECT board_no, m.mem_no, nick, post_ctg, dst1, dst2, title, img, desct, wrt_date, hit_count
FROM board b LEFT JOIN member m
ON b.mem_no = m.mem_no
WHERE post_ctg = #{postCtg}			
AND board_no = #{boardNo}

SELECT mem_no, board_no
FROM recommendation
WHERE board_no = #{boardNo}

mapper.insert:

	SELECT 'BRD' || LPAD(act_seq.nextval, 6, '0')
	FROM dual

INSERT INTO board
VALUES (#{boardNo}
		, #{memNo}
		, #{postCtg}
		, #{dst1}
		, #{dst2}
		, #{title}
		, #{img}
		, #{desct}
		, sysdate
		, 0)
