mapper.insert:

	SELECT 'ACT' || LPAD(act_seq.nextval, 6, '0')
	FROM dual

INSERT INTO auction (
						board_no
						, mem_no
						, title
						, act_date
						, desct
						, sale_qty
						, bnp
						, act_trm
						, ship_price
						, ship_dur_avg
						, ship_dur_max
						, rep
						, reg_stts
					)
VALUES ( #{boardNo}
		 , #{memNo}
		 , #{title}
		 , #{actDate}
		 , #{desct}
		 , #{saleQty}
		 , #{bnp}
		 , #{actTrm}
		 , #{shipPrice}
		 , #{shipDurAvg}
		 , #{shipDurMax}
		 , #{rep}
		 , 'e0'
		)

mapper.select:
{
	CALL buy_Auction_Alert(
		#{memNo, mode=IN, jdbcType=VARCHAR},
		#{boardNo, mode=IN, jdbcType=VARCHAR},
		#{price, mode=IN, jdbcType=INTEGER}
	)
}

{
	CALL bid_Auction_Alert(
		#{memNo, mode=IN, jdbcType=VARCHAR},
		#{boardNo, mode=IN, jdbcType=VARCHAR},
		#{price, mode=IN, jdbcType=INTEGER}
	)
}
