mapper.select:
select crp_sale_no, mem_no, crp_title, crp_ctg, price, qty, rep, reg_date, reg_stts
from crops_sale
where crp_sale_no = #{crpSaleNo}

mapper.update:
update crops_sale


		crp_title = #{crpTitle}


		, qty = #{qty}


		rep = #{rep}


		deta_desct = #{detaDesct}


		reg_date = sysdate


where crp_sale_no = #{crpSaleNo}
