mapper.update:
update crops_sale


		title = #{title}


		, crp_ctg = #{crpCtg}


		, price = #{price}


		, qty = #{qty}


		, rep = #{rep}


		, deta_desct = #{detaDesct}


		, reg_date = sysdate


where board_no = #{boardNo}

update crops_sale
set reg_stts = 'e7'
where board_no = #{boardNo}

mapper.select:
select count(*) "count", nvl(round(avg(rate), 1), 0) "star"
from review
where board_no = #{boardNo}
