mapper.update:
UPDATE funding SET title = #{title}, goal_price = #{goalPrice}, fnd_price = #{fndPrice}, fnd_str_date = #{fndStrDate}, fnd_end_date = #{fndEndDate}, crp_ctg = #{crpCtg}, ship_str_date = #{shipStrDate}, desct = #{desct}
,rep = #{rep}
WHERE board_no = #{boardNo}

UPDATE payment_detail
SET ship_stts = 'B6'
WHERE pay_deta_no = #{payDetaNo}
