mapper.select:
SELECT m.mem_no ,id ,email ,nick ,nvl(login_path,'일반') login_path ,rpt_cnt ,find_code_name(mem_grd) mem_grd ,name ,zip ,addr ,deta_addr ,mbl ,b_date ,find_code_name(gen) gen ,pnt ,find_code_name (stts) stts ,sign_date ,prof ,grd_atch_file ,wdr_apl_date ,blck_date FROM member m LEFT JOIN member_detail d ON(m.mem_no=d.mem_no)
AND NOT mem_grd =find_common_code('관리자')
AND id LIKE '%${id}%'
AND nick LIKE '%${nick}%'
AND mem_grd = #{grd}
AND login_path is null
AND login_path = #{login}
AND stts=#{stts}
AND sign_date > #{str}
AND sign_date < #{end}
ORDER BY sign_date DESC

SELECT inq_no ,mem_no ,find_member_nick(mem_no) name ,inq_title ,find_code_name(inq_ctg) inq_ctg ,inq_desct ,find_code_name(repl_stts) repl_stts ,inq_date ,repl_desct ,repl_date FROM inquiry
AND inq_ctg =#{ctg}
AND repl_stts =#{stts}
AND reg_date > #{str}
AND reg_date < #{end}
ORDER BY inq_no DESC

SELECT board_no ,zip||' '|| addr || ' '|| deta_addr AS addr ,desct ,lend_str_date ,lend_end_date ,lend_price ,atch_no ,area ,reg_date ,find_code_name(reg_stts) reg_stts ,mem_no ,find_member_nick(mem_no) nick , find_code_name(dst1) dst1 , find_code_name(dst2) dst2 ,lati ,longi FROM farm_lend
AND reg_stts= #{stts}
AND reg_date > #{str}
AND reg_date < #{end}
ORDER BY reg_date DESC

SELECT  find_code_name(mcrp) mcrp
FROM MAIN_CROPS
WHERE board_no = #{boardNo}

SELECT board_no ,mem_no ,mkt_ctg ,title ,price ,qty ,rep ,deta_desct ,reg_date ,find_code_name(reg_stts) reg_stts FROM market
AND reg_stts= #{stts}
AND reg_date > #{str}
AND reg_date < #{end}

SELECT board_no ,mem_no ,find_member_nick(mem_no) nick ,title ,desct ,fnd_str_date ,fnd_end_date ,goal_price ,fnd_price ,ship_str_date ,rep ,find_code_name(reg_stts) reg_stts ,find_code_name(crp_ctg) crp_ctg ,find_code_name(fnd_stts) fnd_stts ,reg_date FROM funding
AND reg_stts= #{stts}
AND reg_date > #{str}
AND reg_date < #{end}

SELECT board_no ,mem_no ,find_member_nick(mem_no) nick ,title ,act_date ,desct ,sale_qty ,bnp ,act_trm ,ship_price ,ship_dur_avg ,ship_dur_max ,rep ,find_code_name(reg_stts) reg_stts FROM auction
AND reg_stts= #{stts}
AND act_date = #{str}

SELECT board_no ,mem_no ,find_member_nick(mem_no) nick ,title ,find_code_name(dst1) dst1 ,zip||' '|| addr || ' '|| addr_detail AS addr ,price ,rep ,lend_str_date ,lend_end_date ,deta_desct ,reg_date ,find_code_name(reg_stts) reg_stts ,lend_stts ,lati ,longi FROM farmbnb
AND reg_stts= #{stts}
AND reg_date < #{end}
AND reg_date < #{end}

SELECT board_no ,mem_no ,find_member_nick(mem_no) nick ,title ,rep ,deta_desct ,zip||' '|| addr || ' '|| deta_addr AS addr ,find_code_name(dst1) dst1 ,find_code_name(dst2) dst2 ,ppt_per ,ex_str_date ,ex_end_date ,reg_date ,find_code_name(reg_stts) reg_stts ,lati ,longi FROM experience
AND reg_stts= #{stts}
AND reg_date > #{str}
AND reg_date < #{end}
